export default{
    name:'Steppers',
    data:function(){
        return{
            roadmap:[
                {
                    date:'Aug - 16th',
                    title:'Hackathon Pre Registration Start',
                    endDate:'2022-08-16'
                },
                {
                    date:'Aug - 30th',
                    title:'Sponsors & Track Onboarding Deadline',
                    endDate:'2022-08-30'
                },
                {
                    date:'Sept - 1st',
                    title:'Hackathon Official launch- Registration Start',
                    endDate:'2022-09-01'
                },
                {
                    date:'Sept - 30th',
                    title:'Registration Close',
                    endDate:'2022-09-30'
                },
                {
                    date:'Sept - Oct',
                    title:'Build phase + Sponsor interactions',
                    endDate:'2022-10-31'
                },
                {
                    date:'Oct 31st',
                    title:'Submission Deadline',
                    endDate:'2022-10-31'
                },
                {
                    date:'Nov 1st week',
                    title:'Evaluation',
                    endDate:'2022-11-07'
                },
                {
                    date:'Nov 2nd week',
                    title:'Finale/Demo Day',
                    endDate:'2022-11-14'
                }
            ]
        }
    },
    methods:{
        checkDate(date){
          console.log(new Date('2022-01-25')); 
            //check if date is in the past
            if(date.includes('TBD')){
                return {
                    color:'#CCABE1',

                }
            }else{
                //check if date is past compared to current date
                //dummy date

                var today = new Date();
                var date = new Date(date);
                // console.log(date)
                // console.log(today)
                if(date < today){
                    return {
                        color:'#9FFE00',
                    }
                }
                else{
                    return {
                        color:'#CCABE1',
                    }
                }
            }
        }
    }
}
