.faq-modal {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  min-height: 100vh;
  background: white;
  backdrop-filter: blur(10px);
  z-index: 1000;
  > .faq-container{
      max-width: 1200px;
      margin:0 auto;
    width: 100%;
    height: 100%;
    border-radius: 12px;
    overflow: hidden;
    > .faq-header{
      display: flex;
      justify-content: flex-start;
      align-self: center;
    //   width: 100%;
      padding: 16px 18px;
      // background: linear-gradient(130deg, #f7971e, #ffd200);
      > h2 {
        font-family: 'Brinnan';
        font-style: normal;
        font-weight: 700;
        font-size: 30px;
        line-height: 145.9%;
        /* or 90px */

        letter-spacing: -0.015em;

        color: #36104C;
      }
    }
    > .faq-main {
      padding: 12px 12px 24px;
      height: calc(100% - 64px - 24px);
      text-align: left;
      color: #fff;
      overflow-y: scroll;
      &::-webkit-scrollbar {
        display: none;
      }
      > .faq-item {
        position: relative;
        border-bottom: 0.673529px solid #9B9B9B;
        // border-top: 0.673529px solid #9B9B9B;
        &:first-child {
        //   border-bottom: none;
        border-top: 0.673529px solid #9B9B9B;
        }
        > .accIcon {
          position: absolute;
          cursor: pointer;
        //   top: 50%;
        //   transform: translateY(-50%);
         top:16px;
          right: 0;
          width: 24px;
          height: 24px;
          display: flex;
          align-items: center;
          justify-content: center;
          margin-right: 10px;
          > .vertical,
          > .horizontal {
            content:'';
            position: absolute;
            background-color: rgba(53, 17, 74, 1);
            transition: ease 0.3s all;
          }
          > .vertical {
            width: 2px;
            height: 14px;
          }
          > .horizontal {
            width: 14px;
            height: 2px;
          }
        }
        > .qus {
            cursor: pointer;
              font-size: 14px;
            margin-top: auto;
            font-weight: 600;
            line-height: 24px;
            color: #757575;
            height: 100%;
            margin-bottom: auto;
            padding: 16px 32px;
        }
        > .ans {
          display: none;
          padding-left: 34px;
          margin-bottom: 12px;
          font-size: 12px;
          font-style: normal;
          font-weight: 400;
          line-height: 18px;
          color: rgba(45, 45, 45, 0.8);
          overflow: hidden;
        }
      }
    }
  }
  > .close-btn {
    margin-top: 24px;
    font-family: Poppins;
    font-style: normal;
    font-weight: 600;
    font-size: 16px;
    line-height: 21px;
    color: rgba(53, 17, 74, 1);
    cursor: pointer;
  }
}
