.sponsors{
    background-color: linear-gradient(180deg, #030027 0%, #030027 40.05%);
    min-height: 400px;
    width:calc(100% - 32px);
    padding: 0 16px;
    display: flex;
    flex-direction: column;
    position: relative;
    background-image: url('../../static/bg-building-mobile.png'),linear-gradient(180deg, #030027 0%, #030027 40.05%);;
    background-position: center bottom;
    // background-size: 100% 600px;
    background-repeat: no-repeat;
        .title{
            font-family: 'Brinnan';
            font-style: normal;
            font-weight: 700;
            font-size: 30px;
            line-height: 145.9%;
            letter-spacing: -0.015em;
            color: #FFFFFF;
            padding-left:32px;
            margin-bottom: 25px;
        }
        .wrapper{ 
                display: grid;
                row-gap: 12px;
                column-count: 4;
                width: 100%;
                column-gap: 12px;
                grid-template-columns: auto auto auto auto;
                padding-bottom: 100px;

            .tile{
                position: relative;
                width: 77px;
                height: 75px;
                margin: 0 auto;
                .bg{
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                }
                .sponsor{
                    position: absolute;
                    width:2rem;
                    height: 2rem;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    img{
                        width: 70%;
                        height: 100%;
                        object-fit: contain;
                        transform: scale(3.0);
                    }
                }
            }
            .img{
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
            }
        }
        .wrapper-desktop{ 
                display: none;
                column-count: 3;
                width: 100%;
                grid-template-columns: auto auto auto;
                padding-bottom: 183px;

            .tile{
                position: relative;
                width: 301px;
                height: 108px;
                margin-bottom: 25px;
                .bg{
                    position: absolute;
                    top: 0;
                    left: 0;
                    width: 100%;
                    height: 100%;
                }
                .sponsor{
                    position: absolute;
                    width:100%;
                    height: 100%;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    display: flex;
                    align-items: center;
                    font-family: 'Brinnan';
                    font-style: normal;
                    font-weight: 700;
                    font-size: 18px;
                    line-height: 145.9%;
                    color: #FFFFFF;
                    img{
                        width:100px;
                        height:70px;
                        object-fit: contain;
                        margin-left: 16px;
                    }
                    div{
                        margin-left: 16px;

                        // margin-left: auto;
                        // margin-right: 16px;
                    }

                }
            }
            .img{
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
            }
        }
        .buildings{
            width: 100%;
            margin-top: auto;
            position: absolute;
            bottom: 0;
            left: 0;
        }
        .buildings-desktop{
            display: none;
            position: absolute;
            bottom: 0;
            left: 0;
            // max-height: 200px;
        }

    }
