@media (min-width: 768px) {
    .wrapper{
        width:70%;
        margin: 0 auto;
    }
    .step {
  position: relative;
  min-height: 168px;
  color: grey;
  max-width: 100%;
  display: flex;
  width: 100%;
}
.step + .step {
  margin-top:-10px;
  display: flex;
}
.step > div:first-child {
  position: static;
  height: 0;
}
.step > div:not(:first-child) {
  margin-left: 32px;
}
.step.step-active {
  color: #4285f4
}
.step.step-active .circle {
  background-color: #0069B4;
}
.date{
    margin-right:-17px;
    font-size: 15px;
}
/* Circle */
.circle {
  background: #fff;
  position: relative;
  width: 18.06px;
  height: 18.06px;
  border-radius: 100%;
  color: #fff;
  text-align: center;
}

/* Vertical Line */
.circle:after {
  content: ' ';
  display: block;
  bottom: 1px;
  height: 164px;
  width: 2px;
  background-color: #fff;
  z-index: 3;
  position: absolute;
  top: 0px;
  left: 50%;
  transform: translateX(-50%);
}
.step:last-child .circle:after {
  display: none
}
    .title{
        font-family: 'Brinnan';
        font-style: normal;
        font-weight: 700;
        font-size: 30px;
        line-height: 145.9%;
        max-width: 420px;
        /* or 61px */
        width: 100%;
        letter-spacing: -0.015em;
        color: rgba(255, 255, 255, 0.8);

    }
}
