.mentors{
 background:#220832;   
 min-height: 100vh;
 width: 100%;
 padding-bottom: 128px;
 padding-top: 100px;
 .title{
    font-family: 'Brinnan';
    font-style: normal;
    font-weight: 700;
    font-size: 45px;
    line-height: 145.9%;
    /* or 44px */
    padding-left: 32px;

    letter-spacing: -0.015em;
    padding-top: 48px;
    padding-bottom:16px;
    color: #FFFFFF;
    margin-left: 305px;
    padding-bottom: 5px;
 }
 .sub-title{
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 16px;
    line-height: 145.9%;
    padding-bottom:56px;
    padding-left: 32px;
    margin-left: 305px;
    /* or 20px */


    color: rgba(255, 255, 255, 0.8);  
 }
 .grid{
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    column-gap: 26px;
    /* max-width: 1000px; */
    margin: 0 auto;
    row-gap: 26px;
    justify-items: center;
    max-width: 950px;
    .card{
        width: 216px;
        margin-bottom: 26px;
    }
 }
}
.slider-hooper {
    // margin:0 7px;
    background:#572873;
    border-radius: 9.21867px;
    height:100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;                        
    padding:8px 15px;
    .image{
        height:231px;
        width:100%;
        img{
            width:100%;
            height:100%;
            object-fit: contain;
        }
    }
    .copy{
        display: flex;
        align-items: center;
        justify-content: space-between;
        margin-top: 16px;
        width:100%;
        margin-bottom: 16px;;
        .name{
            font-family: 'Brinnan';
            font-style: normal;
            font-weight: 700;
            font-size: 12.91px;
            line-height: 145.9%;
            /* or 23px */
            text-align: left;
            letter-spacing: -0.015em;
            max-width:180px;

            color: #FFFFFF;
        }
        .position{
            font-family: 'Inter';
            font-style: normal;
            font-weight: 600;
            font-size: 9.5px;
            line-height: 145.9%;
            /* or 18px */
            word-break:break-all;
            letter-spacing: -0.015em;
            max-width:150px;
            color: #21E7FC;
        }
        .logo{
            width:30px;
            border-radius: 2px;
            img{
                width:100%;
                object-fit: cover;
                border-radius: 2px;
            }
        }
    }
}
