.mentors{
 background:#220832;   
 .title{
    font-family: 'Brinnan';
    font-style: normal;
    font-weight: 700;
    font-size: 30px;
    line-height: 145.9%;
    /* or 44px */
    padding-left: 32px;

    letter-spacing: -0.015em;
    padding-top: 48px;
    padding-bottom:16px;
    color: #FFFFFF;
 }
 .sub-title{
    font-family: 'Inter';
    font-style: normal;
    font-weight: 500;
    font-size: 14px;
    line-height: 145.9%;
    padding-bottom:24px;
    padding-left: 32px;
    /* or 20px */


    color: rgba(255, 255, 255, 0.8);  
 }
}
.button-holder{
    display: flex;
    justify-content: flex-end;
    margin-top:55px;
    margin-right:28px;
    padding-bottom: 65px;
    button{
        background: none;
        border: none;
        outline: none;
        cursor: pointer;

    }
}
.slider-hooper {
    margin:0 7px;
    background:#572873;
    border-radius: 9.21867px;
    height:100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;                        
    padding:0 8px;
    .image{
        height:280px;
        width: 94%;
        img{
            width:100%;
            height:100%;
            object-fit: contain;
        }
    }
    .copy{
        display: flex;
        align-items: center;
        justify-content: space-around;
        margin-top: 16px;
        width:100%;
        .name{
            font-family: 'Brinnan';
            font-style: normal;
            font-weight: 700;
            font-size: 16px;
            line-height: 145.9%;
            /* or 23px */
            text-align: left;
            letter-spacing: -0.015em;
            max-width:180px;

            color: #FFFFFF;
        }
        .position{
            font-family: 'Inter';
            font-style: normal;
            font-weight: 600;
            font-size: 12px;
            line-height: 145.9%;
            /* or 18px */
            word-break:break-all;
            letter-spacing: -0.015em;
            max-width:115px;
            color: #21E7FC;
        }
        .logo{
            width:32px;
            img{
                width:100%;
                object-fit: cover;
            }
        }
    }
}
