.wrapper{
    // padding-left: 28px;
    padding-top: 100px;
}
.step {
  position: relative;
  min-height: 118px;
  color: grey;
  max-width: 100%;
  display: flex;
}
.step + .step {
  margin-top:-10px;
  display: flex;
}
.step > div:first-child {
  position: static;
  height: 0;
}
.step > div:not(:first-child) {
  margin-left: 32px;
}
.step.step-active {
  color: #4285f4
}
.step.step-active .circle {
  background-color: #0069B4;
}
.date{
    margin-right:-17px;
}
/* Circle */
.circle {
  background: #fff;
  position: relative;
  width: 12.06px;
  height: 12.06px;
  border-radius: 100%;
  color: #fff;
  text-align: center;
}

/* Vertical Line */
.circle:after {
  content: ' ';
  display: block;
  bottom: 1px;
  height: 114px;
  width: 2px;
  background-color: #fff;
  z-index: 3;
  position: absolute;
  top: 0px;
  left: 50%;
  transform: translateX(-50%);
}
.step:last-child .circle:after {
  display: none
}


/* Stepper Titles */
.title {
    font-family: 'Brinnan';
    font-style: normal;
    font-weight: 700;
    font-size: 16px;
    line-height: 145.9%;
    letter-spacing: -0.015em;
    color: #FFFFFF;
    width: 180px;
}
.date{
    font-family: 'Inter';
    font-style: normal;
    font-weight: 600;
    font-size: 11px;
    line-height: 145.9%;
    text-align: right;
    letter-spacing: -0.015em;
    color: #9FFE00;
    width: 80px;
}
.caption {
  font-size: 0.8em;
}
