.third {
  position: relative;
  min-height: 100vh;
  width: 100%;
  background-image: url("../assets/third/grid-min.png");
  background-position: inherit;
  background-repeat: repeat;
  background-size: contain;
  padding-top: 70px;
  .grid {
    position: absolute;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: -1;
  }
  .content {
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding-bottom: 64px;
    // display:flex;
    // flex-direction:column;
    // align-items:center;
    // justify-content:center;
    z-index: 1;
    .tracks {
      display: flex;
      flex-direction: row;
      gap: 9px;
      justify-content:center;

    }

    .rotating-button {
      width: 145px;
      height: 145px;
      background: #531975;
      border: 1px solid #fdc506;
      font-family: "Brinnan";
      font-style: normal;
      font-weight: 700;
      font-size: 12.6519px;
      line-height: 145.9%;
      letter-spacing: 0.1em;
      color: #fdc506;
      border-radius: 50%;
      justify-content: center;
      align-items: center;
      margin-left: auto;
      margin-right: auto;
      margin-top: 100px;
      cursor: pointer;
      transition: transform 2s ease-in-out;
      display:flex;
    }
    .rotating-button:hover {
      transform: rotate(-360deg);
    }
    .card {
        //place three cards in a row
        display: flex;
        flex-direction: row;
        gap: 7px;
        justify-content: center;
        align-items: center;
        margin-top: 20px;
        margin-bottom: 20px;

      min-height: 250px;
      width: 70%;
      // margin:0 auto;

      max-width: 440px;

      align-items: center;
      justify-content: center;
      // background: linear-gradient(180deg, rgba(90, 62, 158, 0.9) 0%, rgba(60, 41, 116, 0.9) 0.01%, rgba(111, 47, 153, 0.9) 100%);
      background: none;
      opacity: 1;
      border: 0.5px solid white;
      // backdrop-filter: blur(9.75104px);
      border-radius: 9.75104px;
      cursor: pointer;
      .inner-card {
        display: flex;
        flex-direction: column;
        padding: 41px 24px;
        width: calc(100%);
        height: calc(100% - 84px);
        background: linear-gradient(
          180deg,
          rgba(90, 62, 158, 0.9) 0%,
          rgba(60, 41, 116, 0.9) 0.01%,
          rgba(111, 47, 153, 0.9) 100%
        );
        backdrop-filter: blur(9.75104px);
        border-radius: 9.75104px;
        transition: all 0.3s ease-in-out;
        will-change: transform;
        .name {
          display: flex;
          align-items: center;
          font-family: "Brinnan";
          font-style: normal;
          font-weight: 700;
          font-size: 24px;
          line-height: 145.9%;
          letter-spacing: -0.015em;
          color: #ffffff;
          margin-bottom: 23px;
          .t {
            max-width: 300px;
            font-family: "Brinnan";
            font-style: normal;
            font-weight: 700;
            font-size: 24px;
            line-height: 145.9%;
            letter-spacing: -0.015em;
            color: #ffffff;
          }
          .icon {
            min-width: 64.39px;
            height: 64.39px;
            border: 0.60263px solid #ffffff;
            border-radius: 6.0263px;
            margin-right: 23.59px;
            .layer {
              width: 100%;
              height: 100%;
              border-radius: 6.0263px;
              background: white;
              transform: translate3d(-3.59px, -3.59px, -3.59px);
              img {
                width: 100%;
                height: 100%;
                object-fit: cover;
              }
            }
          }
        }
        .details {
          font-family: "Inter";
          font-style: normal;
          font-weight: 500;
          font-size: 14px;
          line-height: 145.9%;
          letter-spacing: -0.015em;
          color: rgba(255, 255, 255, 0.8);
          margin-bottom: 28px;
        }
        .prize {
          margin-bottom: 18px;
          .head {
            font-family: "Inter";
            font-style: italic;
            font-weight: 500;
            font-size: 15.4113px;
            line-height: 145.9%;
            letter-spacing: -0.015em;
            color: #ffffff;
          }
          .amount {
            font-family: "Brinnan";
            font-style: normal;
            font-weight: 700;
            font-size: 45.0484px;
            line-height: 145.9%;
            letter-spacing: -0.015em;
            background: linear-gradient(
              89.97deg,
              #fdc506 1.39%,
              #fd9a06 86.07%
            );
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-clip: text;
            text-fill-color: transparent;
          }
        }
        .buttons {
          font-family: "Brinnan";
          font-style: normal;
          font-weight: 700;
          font-size: 14px;
          display: flex;
          justify-content: space-between;
          align-items: flex-end;
          width: 100%;
          margin-top: auto;
          .learn {
            background: linear-gradient(
              95.7deg,
              #2bf2ff 0.32%,
              #01c3f1 100.26%
            );
            border-radius: 6px;
            height: 40px;
            width: 100%;
            max-width: 235px;
            border: none;
            padding: 16px 8px;
            color: rgba(54, 16, 76, 1);
            display: flex;
            justify-content: space-between;
            align-items: center;
          }
          .discord {
            background: none;
            border-radius: 6px;
            height: 64px;
            width: 45%;
            max-width: 235px;
            border: 1px solid #f0f0f0;
            filter: drop-shadow(0px 0px 15px rgba(43, 217, 255, 0.55));
            padding: 16px 25px;
            color: rgba(255, 255, 255, 1);
          }
        }
        .level {
          height: 36.68px;
          background: rgba(163, 94, 255, 0.6);
          border: 0.916923px solid rgba(255, 255, 255, 0.4);
          border-radius: 3.66769px;
          padding: 6.42px 7.12px;
          font-family: "Brinnan";
          font-style: normal;
          font-weight: 400;
          font-size: 18px;
          line-height: 145.9%;
          color: white;
          display: flex;
          align-items: center;
          justify-content: center;
          margin-bottom: 23px;
        }
        .action {
          font-family: "Inter";
          font-style: normal;
          font-weight: 500;
          font-size: 13px;
          line-height: 145.9%;
          letter-spacing: -0.015em;
          color: #ffffff;
          display: flex;
          margin-bottom: 21px;
          img {
            margin-right: 9px;
          }
          .participants {
            display: flex;
            align-items: center;
            margin-right: 23px;
          }
          .deadline {
            display: flex;
            align-items: center;
          }
        }
        .cta {
          width: 100%;
          max-width: 322px;
          height: 55px;
          padding: 19px;
          border-radius: 6px;
          border: 0.5px solid rgba(3, 197, 242, 1);
          background: none;
          font-family: "Brinnan";
          font-style: normal;
          font-weight: 700;
          font-size: 15.2831px;
          line-height: 145.9%;
          letter-spacing: 0.1em;
          color: #2bf1ff;
        }
      }
      .inner-card:hover {
        transform: translate3d(-15px, -17.52px, -16.69px);
        background: #882eff;
        backdrop-filter: blur(11.5597px);
        -webkit-transition: background backdrop-filter 300ms linear;
        -ms-transition: background backdrop-filter 300ms linear;
        transition: background backdrop-filter 300ms linear;
        .cta {
          background: linear-gradient(91.86deg, #2bf1ff 0.22%, #03c5f3 99.78%);
          box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.25);
          color: #36104c;
        }
      }
    }
