.fourth{
    min-height:100vh;
    width:100%;
    background: linear-gradient(180deg, #5B1D7E, #030027);
    display: flex;
    flex-wrap: wrap;
    .right{
        min-width: 300px;
    }
    .left-w{
        position: -webkit-sticky; /* Safari */
        position: sticky;
        top: 100px;
    }
    .left{
        min-width: 300px;
        max-width: 340px;
        width:100%;
        padding-left: 30px;
        position:relative;
        .title{
            font-family: 'Brinnan';
            font-style: normal;
            font-weight: 700;
            font-size: 30px;
            line-height: 145.9%;
            letter-spacing: -0.015em;
            color: #FFFFFF;
            margin-bottom:21px;
            margin-top: 103px;
        }
        .sub-title{
            font-family: 'Inter';
            font-style: normal;
            font-weight: 500;
            font-size: 14px;
            line-height: 145.9%;
            margin-bottom:51px;
            /* or 20px */


            color: rgba(255, 255, 255, 0.8);
        }
        .s-btn{
            position: relative;
            width:180px;
            height:50px;
            transition: all .3s ease-in-out;
            will-change: transform;
            cursor: pointer;
            font-family: 'Brinnan';
            font-style: normal;
            font-weight: 700;
            line-height: 145.9%;
            font-size: 12px;
            div{
                position: absolute;
                width:100%;
                margin:0 auto;
                height: 100%;
                top:0;
                left:0;
                display: flex;
                align-items: center;
                justify-content: center;

            }
            img{

                object-fit: contain;
            }
            .outline{
                width:100%;
                height: 100%;

            }
            .bg{
                width:100%;
                position: absolute;
                top:0;
                left:0;
                height: 100%;
                object-fit: cover;
                font-family: 'Brinnan';
                font-style: normal;
                font-weight: 700;
                font-size: 11px;
                line-height: 145.9%;
                letter-spacing: 0.1em;
                color: #36104C;
            }
        }
        .s-btn:hover{
            .bg{
                transform: translate3d(-5.23px, -5.27px, -5.27px);
            }
            div{
                transform: translate3d(-5.23px, -5.27px, -5.27px);
            }
        }
        .s-btn:active{
            .bg{
                transform: translate3d(0px, 0px, 0px);
            }
            div{
                transform: translate3d(0px, 0px, 0px);
            }
        }
    }
}
